import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Signin.css';

export default function Signin() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignIn = () => {
        if (username === 'ceng' && password === '123') {
            alert('Successfully signed in!');
            navigate('/machines', { state: { username } });
        } else {
            alert('Invalid username or password');
        }
    };

    return (
        <>
			<Navbar/>
            <div className="container">
                <div className="label-1">
                    <p className="text-1">
                        Sign In
                    </p>
                </div>

                <div className="sign-in">
                    <div className="userName">
                        <label className='signInLabel' htmlFor="username">Username</label>
                        <input
                            className='signInInput'
                            id='username'
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="password">
                        <label className='signInLabel' htmlFor="password">Password</label>
                        <input
                            className='signInInput'
                            id='password'
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button className='signInButton' onClick={handleSignIn}>Sign In</button>
                </div>
                
                <div className="brand">
                    <p className="brandName">MosaicMind</p>
                    <p className="version">V.1</p>
                </div>
            </div>
        </>
    );
}
